    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        String tipo = (String) jComboBox1.getSelectedItem();
        jTextArea1.setText("");
        int poblacion = Integer.parseInt(IPoblacion.getText());
        if(tipo == "Cuantitativa"){
            jCheckBox1.setEnabled(true);
        }else{
            jCheckBox1.setEnabled(false);
        }
        if(poblacion==2) I2.setEnabled(true);
        else I2.setEnabled(false);
        intervalo = new intervalo(poblacion,tipo);
        intervalo.llenarEntradas();
        TeditEntrada.setText("Ingresar:    " + intervalo.getEntradas()[0]);
        if(poblacion==2) {
            jTextArea1.setText("(A)\t(B)\n");
        }else{
            jTextArea1.setText(intervalo.getEntradas()[0]);
        }
    }//GEN-LAST:event_jComboBox1ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int n = intervalo.getN();
        int inicio = intervalo.getInicio() +1;

        if(inicio < n){

            int poblacion = intervalo.getPoblaciones();
            if(poblacion==2)
            intervalo.llenar2(Double.parseDouble(I2.getText()));
            intervalo.llenar1(Double.parseDouble(I1.getText()));
            if(poblacion==2) {
                jTextArea1.append(intervalo.getEntradas()[inicio] +"=" +intervalo.getValor1(inicio)+ "\t");
                jTextArea1.append(intervalo.getEntradas()[inicio] +"=" +intervalo.getValor2(inicio) + "\n");
            }else {
                jTextArea1.append("=" + intervalo.getValor1(inicio));
                if(inicio < n-1){
                    jTextArea1.append("\n" + intervalo.getEntradas()[inicio+1]);
                }
            }
            if(inicio < n-1){
                TeditEntrada.setText("Ingresar:      " + intervalo.getEntradas()[inicio+1]);
            }
            I1.setText("");
            I2.setText("");

        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jCheckBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCheckBox1ActionPerformed
        intervalo.setVarianzaPobCo((jCheckBox1.isSelected()));
        intervalo.llenarEntradas();
    }//GEN-LAST:event_jCheckBox1ActionPerformed

