    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        intervalo.setTabla();
        intervalo.setDecimales(Integer.parseInt(Idecimales.getText()));
        intervalo.setDesconfianza(Double.parseDouble(Idesconfianza.getText()));     
        String tipo = intervalo.getTipo();
        Tedit1.setIcon(formula.getIconLaTex(intervalo.getTablaFormula(true),15));
        if(tipo=="Varianza"){
            Itabla2.setEnabled(true);            
            Tedit2.setIcon(formula.getIconLaTex(intervalo.getTablaFormula(false),20));      
        }else{
             Itabla2.setEnabled(false);
             Tedit2.setIcon(null);  
        }
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String tipo = intervalo.getTipo();
        intervalo.setValorTabla(Double.parseDouble(Itabla1.getText()));
        if(tipo=="Varianza" ){
            intervalo.setValorTabla2(Double.parseDouble(Itabla2.getText()));
        }
    }//GEN-LAST:event_jButton3ActionPerformed

