    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        TeditResul.setIcon(formula.getIconLaTex(intervalo.getFormulaText(),25));
        String tipo = intervalo.getTipo();
        int poblaciones = intervalo.getPoblaciones();
        boolean varianzaPobCo = intervalo.isVarianzaPobCo();
        if(tipo=="Cuantitativa" && poblaciones == 2 && varianzaPobCo==false)            
            jLabel9.setIcon(formula.getIconLaTex(intervalo.spCuadradoText(),20));
        else jLabel9.setIcon(null); 
    }//GEN-LAST:event_jButton4ActionPerformed

